// smart contracts is a place where we write our business logic
// declaring version of solidity
pragma solidity ^0.5.0;

// contract is a class
contract Election{
    // model a candidate
    struct Candidate
    {
        uint id;
        uint documentVerified;
        uint age;
        string name;
        uint voteCount;

    }
    // voters who have already voted
    mapping(address=>bool) public voters;
    // store candidate using a hash 
    mapping(uint=>Candidate) public candidates;
    //mapping cannot  be iterated and it has no size method
    event votedEvent(
        uint indexed _candidateId
    );
    // count of candidates
    // default value is 0
    uint public CandidatesCount;
    // constructor
    constructor() public{
        // constructor invoking candidates
        addCandidate("Candidate 1",25);
        addCandidate("Candidate 2",24);
        addCandidate("Candidate 3",22);

        
    }
    // we only want our contract to invoke this function so we make it private
    function addCandidate(string memory _name,uint _age) private{
        // increment the count of candidates
        CandidatesCount++;
        // refer candidates mapping
        candidates[CandidatesCount] = Candidate(CandidatesCount,0,_age, _name,0);
    }
      // function to vote a candidate
    function vote(uint _candidateId) public {
        // haven't voted before
        require(!voters[msg.sender]);
        // a valid candidate
        require(_candidateId > 0 && _candidateId <= CandidatesCount);
    // record that voter has voted
       voters[msg.sender] = true;
     // update vote count
       candidates[_candidateId].voteCount++;
     // trigger voted event
     emit votedEvent(_candidateId);


    }
}
